postNclood = function() {

  /*
    wtc.Nintendo.Omni.trackPlayNintendoPage
    -------------
    Bootstrapping the track Play Nintendo page into the
    provided omniture suite because we want to
    transparently split the parents suite from the kids'.
   */
  var e, request;
  window.wtc.Nintendo.Omni.trackPlayNintendoPage = function(path, params) {
    var bits, isHome, pagename, precurser;
    isHome = false;
    precurser = '';
    URLComps = /\/?(parents\/)?(.*)\//.exec(window.location.pathname);
    isHome = false;
    if (URLComps[2]) {
      bits = URLComps[2].split('/');
      pagename = precurser + ':' + bits.join(':');
    } else {
      pagename = precurser + ':home';
      isHome = true;
    }
    this.trackPage(pagename, isHome);
  };

  /*
    Omniture setup and attribution
    -------------
    First we track the page, which sets up all of the basic
    parameters for omniture, then we attribute omniture
    callbacks to various places
   */
  if (document.body.className.indexOf('noPLAYPageTrack') < 0) {
    try {
      window.wtc.Nintendo.Omni.trackPlayNintendoPage(window.wtc.pageTrackName, window.wtc.pageTrackParams);
    } catch (error) {
      e = error;
      console.log('error in omniture');
    }
  }
  if (!window.OMNITRACK) {
    window.OMNITRACK = function(eVar, eVarData, events, name) {
      return window.wtc.Nintendo.Omni.trackLink(eVar, eVarData, events, name);
    };
  }
  runSizmek();
  nintendoInit();
  try {
    request = new XMLHttpRequest();
    request.open('POST', "/api/v1/stat/pagehit/?path=" + window.location.pathname, true);
    request.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded; charset=UTF-8');
    request.setRequestHeader('X-CSRFToken', window.wtc.getCookie('csrftoken'));
    return request.send({
      path: window.location.pathname
    });
  } catch (error) {
    e = error;
  }
};


