/*
  Angular Filter - Test for duplicate content
  =======================================
  - *Author*          liamegan
  - *email*           liam@wethecollective.com
  - *Created*         2015-05-19 13:04:01
  - *namespace*       window
  - *Requirements*    n/a
  - *Description*     An angular filter that removes IDs that are duplicated in the theme list
  - *Edited by*       liamegan
  - *Edited*          2015-05-19 13:36:22
  - *Version*         0.1
 */
(function(app) {
  return app.filter('duplicateTheme', function() {
    return function(items) {
      var filtered, ids;
      filtered = [];
      ids = '_';
      angular.forEach(items, function(item) {
        if (item.source === 'history') {
          return;
        }
        if (ids.indexOf("_" + item.id + "_") === -1) {
          filtered.push(item);
          ids = ids + item.id + '_';
        }
      });
      return filtered;
    };
  });
})(window.PlayNintendo.ngApp);


