/*
    Angular Directive - Play Theme Container
    =======================================
  - *Author*          liamegan
  - *email*           liam@wethecollective.com
  - *Created*         2014-06-09 07:40:42
  - *namespace*       window
  - *Requirements*    n/a
  - *Description*     The theme container directive
  - *Edited by*       liamegan
  - *Edited*          2015-05-07 16:51:18
  - *Version*         1.0
 */
(function(app) {
  return app.directive('playThemeContainer', function() {
    return {
      restrict: 'E',
      controller: [
        '$scope', '$element', '$attrs', function(scope, element, attrs) {
          scope.filters = [];
          this.filterTheme = function(filters) {
            scope.filters = filters;
          };
          scope.$on('FILTER_UPDATED', function(event, data) {
            return scope.$broadcast('FILTER_APPLIED', data);
          });
        }
      ],
      link: function(scope, elem, attrs) {}
    };
  });
})(window.PlayNintendo.ngApp);


