/*
  Angular Directive - Play Nintendo Light or Dark
  =======================================
  - *Author*          liamegan
  - *email*           liam@wethecollective.com
  - *Created*         2014-06-09 07:40:42
  - *namespace*       window
  - *Requirements*    n/a
  - *Description*     An angular directive that sets the class of an element to light or dark based on the supplied background colour.
  - *Edited by*       liamegan
  - *Edited*          2014-09-10 15:56:41
  - *Version*         0.1
 */
(function(app) {
  return app.directive('playLightOrDark', function() {
    return {
      scope: true,
      restrict: 'A',
      link: function(scope, elem, attrs) {
        var color, colour, distance, hexcomponents, rgb;
        colour = attrs.playLightOrDark;
        hexcomponents = /#([0-9ABCDEFabcdef]{1,2})([0-9ABCDEFabcdef]{1,2})([0-9ABCDEFabcdef]{1,2})/.exec(colour);
        if (!hexcomponents) {
          return '';
        }
        rgb = [eval("0x" + hexcomponents[1]), eval("0x" + hexcomponents[2]), eval("0x" + hexcomponents[3])];
        distance = Math.sqrt(rgb[0] * rgb[0] * .241 + rgb[1] * rgb[1] * .691 + rgb[2] * rgb[2] * .068) / 255 * 100;
        if (distance > 75) {
          color = 'dark';
        } else {
          color = 'light';
        }
        if (elem[0].classList) {
          elem[0].classList.add(color);
        } else {
          elem[0].className += ' ' + color;
        }
        if (attrs.transition) {
          if (elem[0].classList) {
            elem[0].classList.add('transition');
          } else {
            elem[0].className += ' transition';
          }
          setTimeout(function() {
            return TweenLite.to(elem[0], 1, {
              backgroundColor: colour
            });
          }, 1000);
        }
      }
    };
  });
})(window.PlayNintendo.ngApp);


