(function(app, swipe) {
  return app.directive('playThemeFilter', function() {
    return {
      restrict: 'E',
      replace: true,
      template: window.wtc.TEMPLATES.playThemeFilter,
      link: function(scope, el, attrs, playThemeContainerController) {
        var clear_selected_filter, filter_tags, filter_tags_mobile;
        filter_tags = null;
        filter_tags_mobile = null;
        scope.$watch('filters', function(newData) {
          if (typeof newData === 'object' && newData.count > 0) {
            return setTimeout(function() {
              var i, mobile_filter_container, mobile_filter_open, results;
              mobile_filter_open = false;
              filter_tags = document.querySelector('.theme-filter ul').getElementsByTagName('li');
              mobile_filter_container = document.querySelector('.theme-filter-mobile ul');
              filter_tags_mobile = mobile_filter_container.getElementsByTagName('li');
              i = 0;
              while (i < filter_tags.length) {
                filter_tags[i].addEventListener('click', function() {
                  var index, parent;
                  clear_selected_filter();
                  this.className += " active";
                  parent = this.parentNode;
                  index = Array.prototype.indexOf.call(parent.children, this);
                  return filter_tags_mobile[index].className += " active";
                });
                i++;
              }
              i = 0;
              results = [];
              while (i < filter_tags_mobile.length) {
                filter_tags_mobile[i].addEventListener('click', function() {
                  var index, parent;
                  if (mobile_filter_open === false) {
                    mobile_filter_open = true;
                    return mobile_filter_container.classList.add('open');
                  } else {
                    mobile_filter_open = false;
                    mobile_filter_container.classList.remove('open');
                    clear_selected_filter();
                    this.className += " active";
                    parent = this.parentNode;
                    index = Array.prototype.indexOf.call(parent.children, this);
                    return filter_tags[index].className += " active";
                  }
                });
                results.push(i++);
              }
              return results;
            }, 1000);
          }
        });
        return clear_selected_filter = function() {
          var i, results;
          i = 0;
          results = [];
          while (i < filter_tags.length) {
            filter_tags[i].classList.remove('active');
            filter_tags_mobile[i].classList.remove('active');
            results.push(i++);
          }
          return results;
        };
      }
    };
  });
})(window.PlayNintendo.ngApp, window.Swipe);


