/*
  Angular Directive - Resizable
  =======================================
  - *Author*          liamegan
  - *email*           liam@wethecollective.com
  - *Created*         2014-06-09 07:40:42
  - *namespace*       window
  - *Requirements*    n/a
  - *Description*     An angular directive that sets an element to react to resize callbacks
  - *Edited by*       liamegan
  - *Edited*          2014-08-27 10:12:51
  - *Version*         0.9
 */
(function(app) {
  return app.directive('resizable', [
    '$window', function($window) {
      return function($scope, $element) {
        var triggerResize;
        angular.element($window).bind('resize', function(e) {
          var BP, columns, container_width, direction, element, elements, fixels, h, i, importance, j, len, rows, sizer, spare, w, width;
          elements = document.getElementById('grid-container').querySelectorAll('div.grid div.ngrid');
          sizer = document.querySelector('div.sizer');
          width = sizer.offsetWidth;
          BP = window.wtc.responsive.Breakpoints.getBreakpoint();
          columns = PlayNintendo.CONST.GRID[BP];
          window.el = $element;
          rows = $element[0].offsetHeight;
          container_width = width * columns;
          fixels = 0;
          $element.css('width', container_width + "px");
          for (i = j = 0, len = elements.length; j < len; i = ++j) {
            element = elements[i];
            importance = (/importance-([1-3])/.exec(element.className) || [0, false])[1];
            direction = (/direction-(h|v)/.exec(element.className) || [0, false])[1];
            w = 0;
            h = 0;
            if (importance === "1") {
              h = width * 2;
              w = width * 2;
              fixels += 4;
            } else if (importance === "2") {
              if (direction === 'h') {
                h = width;
                w = width * 2;
              } else if (direction === 'v') {
                h = width * 2;
                w = width;
              }
              fixels += 2;
            } else {
              h = width;
              w = width;
              fixels += 1;
            }
            element.style.width = w + "px";
            element.style.height = h + "px";
            element.style.paddingBottom = 0;
          }
          if (window.PlayNintendo.isotope) {
            window.PlayNintendo.isotope.arrange();
          }
          spare = window.PlayNintendo.getFixelSize() - fixels;
        });
        triggerResize = function() {
          var event;
          event = document.createEvent('HTMLEvents');
          event.initEvent('resize', true, false);
          window.dispatchEvent(event);
        };
        setTimeout((function(_this) {
          return function() {
            return triggerResize();
          };
        })(this), 100);
        return setTimeout((function(_this) {
          return function() {
            return triggerResize();
          };
        })(this), 1000);
      };
    }
  ]);
})(window.PlayNintendo.ngApp);


